    let index = testStr.indexOf("speed=");
    testStr = testStr.substr(index);
    testStr = testStr.substr(6).replace('x','');
    let indexBr = bitStr.indexOf("bitrate=");
    bitStr = bitStr.substr(indexBr);
    let splits = bitStr.split("dup=");
    bitStr = splits[0]
    let speed = parseFloat(testStr);
    if(speed < 0.95 ){
        msg.fcolor = "red";
    }
    if (speed >= 0.96 && speed < 1.5) {
        msg.fcolor = "limegreen";
    }
    msg.payload = `speed=${speed}x, ${bitStr}`;
    return msg;
}

