if (msg.payload) {
    global.set("bitratechange", false);
    let sr = parseInt(global.get("settings.device.tx.dvbs2.sr")) / 1000;
    global.set("settings.SR", sr);
    let audioBitRate = parseInt(global.get("settings.ABITRATE"));
    let videoBitRate = 0;
    let fec = global.get("settings.device.tx.dvbs2.fec");
    let modulation = global.get("settings.device.tx.dvbs2.constel");
    // ALL THE CALCULATIONS ARE GONE!!! XD
    let codec = global.get("settings.CODEC");
    let TSBitRate = global.get("settings.device.tx.dvbs2.ts.bitrate") / 1000;
    //Decrease TSBitRate for GSE to 88% because of the IP-Header (internal encoder)
    let streamout = global.get("settings.device.tx.stream.mode");
    if (streamout == "dvbs2-gse")
        TSBitRate = (TSBitRate * 0.88)

    if (sr > 20 && sr < 36)
        videoBitRate = (TSBitRate * 0.50) - audioBitRate;
    if (sr > 35 && sr < 67)
        videoBitRate = (TSBitRate * 0.56) - audioBitRate;
    if (sr > 66 && sr < 126)
        videoBitRate = (TSBitRate * 0.66) - audioBitRate;
    if (sr > 125 && sr < 251)
        videoBitRate = (TSBitRate * 0.75) - audioBitRate;
    if (sr > 250 && sr < 334)
        videoBitRate = (TSBitRate * 0.81) - audioBitRate;
    if (sr > 333 && sr < 501)
        videoBitRate = (TSBitRate * 0.84) - audioBitRate;
    if (sr > 500 && sr < 1001)
        videoBitRate = (TSBitRate * 0.87) - audioBitRate;
    if (sr > 1000 && sr < 1501)
        videoBitRate = (TSBitRate * 0.88) - audioBitRate;
    if (sr > 1500 && sr < 3001)
        videoBitRate = (TSBitRate * 0.88) - audioBitRate;
    switch (modulation) {
        case "qpsk":
            if (fec === "1/4") videoBitRate = (videoBitRate * 0.80);
            if (fec === "1/3") videoBitRate = (videoBitRate * 0.87);
            if (fec === "2/5") videoBitRate = (videoBitRate * 0.91);
            if (fec === "1/2") videoBitRate = (videoBitRate * 0.94);
            if (fec === "3/5") videoBitRate = (videoBitRate * 0.96);
            if (fec === "2/3") videoBitRate = (videoBitRate * 0.98);
            break;
        case "8psk":
            if (fec === "3/5") videoBitRate = videoBitRate * 1.01;
            if (fec === "2/3") videoBitRate = videoBitRate * 1.01;
            if (fec === "3/4") videoBitRate = videoBitRate * 1.01;
            if (fec === "5/6") videoBitRate = videoBitRate * 1.02;
            if (fec === "8/9") videoBitRate = videoBitRate * 1.02;
            if (fec === "9/10") videoBitRate = videoBitRate * 1.02;
            break;
        case "16apsk":
            if (fec === "2/3") videoBitRate = videoBitRate * 1.03;
            if (fec === "3/4") videoBitRate = videoBitRate * 1.03;
            if (fec === "5/6") videoBitRate = videoBitRate * 1.03;
            if (fec === "8/9") videoBitRate = videoBitRate * 1.03;
            if (fec === "9/10") videoBitRate = videoBitRate * 1.03;
            break;
        case "32apsk":
            if (fec === "3/4") videoBitRate = videoBitRate * 1.04;
            if (fec === "5/6") videoBitRate = videoBitRate * 1.04;
            if (fec === "8/9") videoBitRate = videoBitRate * 1.04;
            if (fec === "9/10") videoBitRate = videoBitRate * 1.04;
            break;
    }
    if (videoBitRate > 1 && videoBitRate < 61) videoBitRate = (videoBitRate * 0.5);
    if (videoBitRate > 60 && videoBitRate < 101) videoBitRate = (videoBitRate * 0.7);
    // if (codec === "libvvenc") videoBitRate = videoBitRate * 0.95;   
    global.set("settings.TSBITRATE", TSBitRate);
    msg.payload = TSBitRate;
    global.set("settings.VBITRATE", Number(videoBitRate.toFixed(2)));
    return msg;
}

