    msg.services = ts_duck.services;
    for (let index = 0; index < ts_duck.services?.length; index++) {
        const service = ts_duck.services[index];
        msg[`service_${index}`] = service;
    }
}
if (ts_duck?.pids) {
    for (let index = 0; index < ts_duck.pids.length; index++) {
        const pid = ts_duck.pids[index];
        let beacon = false;
        if (msg.service_0?.name === "A71A") {
            beacon = true;
            if (pid.id === 257) msg.video = pid;
            if (pid.id === 258) msg.audio = pid;
        }
        if (pid.id === 256 && !beacon) {
            if (pid.audio) msg.audio = pid;
            if (pid.video) msg.video = pid;
        }
        if (pid.id === 257 && !beacon) {
            if (pid.audio) msg.audio = pid;
            if (pid.video) msg.video = pid;
        }
    }
    msg.payload = ts_duck.pids;
    msg.ts = ts_duck.ts;
    return msg;
}

