    if (sr > 333 && sr < 501) {
        videoBitRate = (1000 * qpskTsBitRate * 82 / 100 / 1000) - audioBitRate;
    }
    if (sr > 500 && sr < 1001) {
        videoBitRate = (1000 * qpskTsBitRate * 86 / 100 / 1000) - audioBitRate;
    }
    if (sr > 1000 && sr < 1501) {
        videoBitRate = (1000 * qpskTsBitRate * 87 / 100 / 1000) - audioBitRate;
    }
    if (sr > 1500 && sr < 3001) {
        videoBitRate = (1000 * qpskTsBitRate * 87 / 100 / 1000) - audioBitRate;
    }
    msg.payload = qpskTsBitRate.toFixed(2);
