    // This need to check if these aren't null / undefined
    device.tx.frequency === "2450000000" &&
    device.tx.stream.mode === "pass" &&
    device.tx.gain === "-89.00" &&
    global.get("mute") === "0"
) {
    msg.payload = "go";
